vec3 c2to3(vec2 z) {
    return vec3(z.x*2., vec2(1,-1)*z.y*sqrt(3.)-z.x)/3.;
}

//(a+bS+cS2)(d+eS+fS2) =
// ad + ae S + af S2 + 
// bf + bd S + be S2 + 
// ce + cf S + cd S2

vec3 mul(vec3 a, vec3 b) {
    return mat3(b.xyz,b.zxy,b.yzx)*a;
}

//just switch the two complex components to take the conjugate

vec3 conj(vec3 z) {
    return z.xzy;
}

float sqmag(vec3 z) {
    return mul(z,conj(z)).x;
}

vec3 inv(vec3 z) {
    return conj(z)/sqmag(z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 R = iResolution.xy,
    c2 = (4.*(fragCoord -.5*R)) / R.x-vec2(.2,0);
    vec3 c3 = c2to3(c2);
    vec3 z = vec3(0);
    fragColor = vec4(c2to3(c2),1.);
    for(int i = 0; i < 50; i++) {
        z = mul(z,z)+ c3;
        if(sqmag(z)>4.){
            fragColor = vec4(.5+.05*sin(float(i)*vec3(.1,.2,.3))*(z+10.),1.);
            break;
        }
    }




}
