vec4 hi_green = vec4(.341,1.,0.,1.);
vec4 white =  vec4(1.,1.,1.,1.);

float LINES_NUMBER = 40.;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 sc = vec2 (iResolution.x / iResolution.y, 1.);
    //
    float set = 0.;
    float shift = iTime * 3.;

    //if (uv.x<=0.5) shift = -shift+1./LINES_NUMBER;

    //float test = floor((uv.x + shift)*LINES_NUMBER);
    float test = floor(length ((uv - vec2 (0.5, 0.5)) * sc)*LINES_NUMBER + shift  );
    if (mod( test, 2.) == 1.)  set = 1.;

    vec4 color = white * set;

    if (length ((uv - vec2 (0.5, 0.5)) * sc ) < 0.014) color = vec4(1.,0.,0.,1.);

	fragColor = color;
}
