float sdFloor(vec3 p){
    return p.y;
}
float plane(vec3 p,vec3 po,vec3 n){
    n=normalize(n);
    vec3 dp=po-p;
    return dot(dp,n);
}
float sphere(vec3 p,float r){
    return length(p)-r;
}
float line(vec3 p,vec3 po,vec3 n){
    n=normalize(n);
    vec3 dp=po-p;
    vec3 prj_dp=dot(n,dp)*n;
    vec3 d=dp-prj_dp;
    return length(d);
}
float cylinder(vec3 p,vec3 po,vec3 n,float r){
    return line(p,po,n)-r;
}
float cone(vec3 p,vec3 po,vec3 n,float dr){
    n=normalize(n);
    vec3 dp=po-p;
    vec3 prj_dp=dot(n,dp)*n;
    float r=length(prj_dp)*dr;
    vec3 d=dp-prj_dp;
    return length(d)-r;
}
float map(vec3 p){
    vec3 q=fract(p)*2.0-1.0;
    float dl=cone(p,vec3(0,0,0),vec3(0,1,0),0.25);
    float dc=cylinder(p,vec3(0,0,0),vec3(1,0,0),0.2);
    //return min(min(min(dl,sphere(p,0.5)),dc),sdFloor(p));
    return min(min(dl,sphere(p,0.5)),dc);
}
vec3 grad(vec3 p){
    vec3 eps = vec3( 0.001, 0.0, 0.0 );
    float d=map(p);
	vec3 g = vec3(
	    map(p+eps.xyy) - d,
	    map(p+eps.yxy) - d,
	    map(p+eps.yyx) - d );
	return g;
}
float trace(vec3 o,vec3 r){
    float t=0.0;
    for(int i=0;i<128;i++){
        vec3 p=o+r*t;
        float d=map(p);
        t+=d*0.95;
    }
    return t;
}
mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv=uv*2.0-1.0;
    uv.x*=iResolution.x/iResolution.y;
    vec2 mo = iMouse.xy/iResolution.xy;

	float time = iTime;

	// camera	
	vec3 ro = vec3(3.5*cos(0.1*time + 6.0*mo.x), 10.0 - 10.0*mo.y, 3.5*sin(0.1*time + 6.0*mo.x) );
	vec3 ta = vec3( 0.0, .0, 0.0 );

	// camera-to-world transformation
    mat3 ca = setCamera( ro, ta, 0.0 );

    // ray direction
	vec3 rd = ca * normalize( vec3(uv.xy,2.0) );
    vec3 r=normalize(vec3(uv,1.0));
    float the=iTime;
    //r.xz*=mat2(cos(the),-sin(the),sin(the),cos(the));
    vec3 o=vec3(0.0,0.0,-3);
    float t=trace(ro,rd);
    vec3 p=ro+rd*t;
    vec3 l=normalize(vec3(0.0,1.0,-1.0));
    vec3 n=normalize(grad(p));
    float dif=dot(l,n);
    float fog=1.0/(1.0+t*t*0.1);
    //vec3 fc=vec3(fog);
    vec3 fc=vec3(dif,dif,0.0);
	fragColor = vec4(fc,1.0);
}
