float deres( float value, int res ) {
    return floor(value / float(res)) * float(res);
}

vec2 deres( vec2 vector, int res) {
    return vec2(deres(vector.x, res), deres(vector.y, res));
}

int modfloor( float value, float m ) {
    return int(mod(floor(value), m));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int res = (modfloor(iTime,5.0)+1) * (modfloor(iTime, 7.0)+1);

	vec2 uv = deres(fragCoord.xy, res) / iResolution.xy;
    float v = uv.x + uv.y;
	fragColor = vec4(
        abs(sin((v+1.0)*iTime)),
        abs(sin((v+2.0)*iTime)),
        abs(sin((v+3.0)*iTime)),
        1.0
    );
}
