void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

    vec4 black = vec4(0.0, 0.0, 0.0, 1.0);
    vec4 white = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 clear = vec4(0.5, 0.5, 0.5, 1.0);
    vec4 texture1 = texture(iChannel0, uv);
    vec4 texture2 = texture(iChannel1, uv); 
    const int numRows = 6;			
    const int numCols = 4;			
    const bool bonus = false;

    if (bonus)
    {
        black = texture1;
        white = texture2;
    }

    for (int i = 0; i < numRows; i++)
    {
        for (int j = 0; j < numCols; j++)
        {
            float x = float(i);
            float y = float(j);

            float leftBound = 1.0 * (x / float(numRows));
            float rightBound = 1.0 * (x / float(numRows)) + 1.0 / float(numRows);
            float upBound = 1.0 * (y / float(numCols));
            float downBound = 1.0 * (y / float(numCols)) + 1.0 / float(numCols);

            if (uv.x > leftBound && uv.x < rightBound)
            {
                if (uv.y > upBound && uv.y < downBound)
                {
                    if (mod(float(i), float(2)) == 0.0)
                    {
                        if (mod(float(j), float(2)) == 0.0)
                        {
                            fragColor = black;
                            break;
                        }
                        else
                        {
                            fragColor = white;
                            break;
                        }
                    }
                    else
                    {
                        if (mod(float(j), float(2)) == 0.0)
                        {
                            fragColor = white;
                            break;
                        }
                        else
                        {
                            fragColor = black;
                            break;
                        }
                    }
                }
            }
        }
    }
}
