void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float propx = iResolution.x/iResolution.y;
    float cellx = floor(propx * 64.0);

    float propy = 1.0/propx;
    float celly = floor(propy * 64.0);

    //int c1 = int(floor((uv.y*celly)) + floor(iTime * 4.0));
    //int xo = c1 & 32;

    //int c2 = int(floor((uv.x*cellx)) + floor(iTime * 4.0));
    //xo = (c2 & 1) >> (c1 & 8);


    int counter1 = int(floor(iTime*14.0) 
                      - floor(uv.x * cellx) 
                      + floor(uv.y * celly)) 
                      & 8;

    int counter2 = int(floor(iTime*12.0) 
                      + floor(uv.x * cellx)) 
                      & 234;

    int counter3 = int(floor(uv.x * cellx)) 
        			  & 50;

    int shift1 = (int(floor(uv.x * cellx) 
                     + floor(uv.y * celly))) 
        			 & 9;

    int shift2 = (int(floor(uv.x * cellx) 
                     - ceil(uv.y * celly))) 
        			 & 7;

    int on = (counter1 >> shift1) & 12;
    int no = (counter2 << shift2) & 13;
    int xo = no ^ on;

    xo = xo ^ counter3 & counter2;


    fragColor = vec4(vec3(1.0-float(xo)),1.0);
}
