#define FREQUENCY 90.
#define SPEED 0.05
#define TOP_COLOR vec4(1)
#define BOTTOM_COLOR vec4(1,0,0,1)

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
 	fragCoord /= iResolution.y;    
    float u = fragCoord.y*2.;

    fragCoord *= mat2(1,-1,1,1) / sqrt(2.);
    fragCoord.y += iTime * SPEED;
    fragCoord = sin (fragCoord * FREQUENCY);

    u = pow( smoothstep(0., 1., u + fragCoord.x*fragCoord.y ), 64.);

    fragColor = mix(TOP_COLOR, BOTTOM_COLOR, u);
}
