void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0., 0., 0., 1.);
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
	vec2 circle = vec2(0., 0.);
    float radius = .3f;
    float size = .05f;
    float len = length(circle - uv);
    if (len <= radius + size) {
        for (float i = 1.; i <= 10.; i++) {
    	float currentRadius = radius * abs(sin(iTime + i));
        	if ((len <= currentRadius + size / i) && (len >= currentRadius - size / i)) {
        		fragColor.r = (abs(radius - len) / radius) - 
               (abs(currentRadius - len)) / (currentRadius);
               	fragColor.b = (abs(radius - len) / radius) - 
               (abs(currentRadius - len)) / (currentRadius) + sin(iTime + i);
               	fragColor.g = (abs(radius - len) / radius) - 
               (abs(currentRadius - len)) / (currentRadius) + cos(iTime + i);
        	}
        }
    }

}
