float simpleMap(float dIn, float oMin, float oMax){
    return dIn * (oMax - oMin) + oMin;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;

    float cf = simpleMap(sin(simpleMap(uv.x, 0., 2.)), 20., 30.);
    cf = cf * simpleMap(uv.y, 0.7, 1.);

    float c0 = tan(iTime-uv.x*cf + uv.y*cf)/2.+0.5;
    float c1 = tan(iTime+uv.x*cf + uv.y*cf)/2.+0.5;

    float v0 = (c0 + c1) * (0.0 + simpleMap(uv.y, .3, 1.));
    float v1 = (c1 - c0) * 0.9;
    float v2 = cos(iTime) * 0.3;

    fragColor = vec4(v0, v1, v2, 1.0);
}
