#define _SnowflakeAmount 10	// Number of snowflakes
#define _BlizardFactor 0.1		// Fury of the storm !

vec2 uv;

float rnd(float x)
{
    return fract(sin(dot(vec2(x+47.49,38.2467/(x+2.3)), vec2(12.9898, 78.233)))* (43758.5453));
}

float drawCircle(vec2 center, float radius)
{
    return 1.0 - smoothstep(0.0, radius, length(uv - center));
}


float drawCC(float p){
    float j;
    float color = 0.;
    for(int i=0; i<_SnowflakeAmount; i++)
    {
        j = float(i);
        float a = 0.0;
        float b = 0.0;

        float c = -1.0* 3.14/4.0;
        if(mod(p,2.0)==0.0){
            c +=  j*3.14/20.0;
        }
        else{
            c +=  (j + 0.5)*3.14/20.0;
        }
        float m = sin(fract(((iTime+p)*0.05))) ;
        float x = 0.9 *m;  
        float y = 0.9 *m;
        float x1=(x-a)*cos(c)-(y-b)*sin(c)+a;
        float y1=(y-b)*cos(c)+(x-a)*sin(c)+b;
//      float speed =  0.3+rnd(cos(j))*(0.7+0.5*cos(j/(float(_SnowflakeAmount)*0.25)));
        vec2 center = vec2(x1,y1);//vec2((0.25-uv.y)*_BlizardFactor+rnd(j)+0.1*cos(iTime+sin(j)), mod(sin(j)-speed*(iTime*1.5*(0.1+_BlizardFactor)), 0.65));
        color += drawCircle(center,0.01*(sin(3.0*m)))*(1.0 - sin(1.5*m)) ;
     }
        return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uv = fragCoord.xy / iResolution.x;
   // uv -= 0.5;
    //uv.x *= iResolution.x/iResolution.y;
    vec3 col =  vec3(0.5,0.35,0.79) ;//0.5 + 0.5*cos(0.01*iTime+uv.xyx+vec3(128,92,203));

    fragColor = vec4(col, 1.0);
    float j;

    for(float i=0.; i<20.; i++)
    {  
        fragColor += vec4(drawCC(i));
    }
}
