#define PI 3.14159
float numflaps = 3.0;
float amplitude = 0.1;
float frequency = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.y += amplitude * uv.x * sin(frequency * 2.0*PI*iTime - uv.x*numflaps * 2.0*PI);

    fragColor = texture(iChannel0, uv);
}
