void mainImage(out vec4 o, vec2 U) { 
	vec2 u = (U/iResolution.y)*6.;
    int t = int(iTime*3.)%11,
    c = int[](8,5,12,12,15,23,15,18,12,4,-31)[t];
	if(abs(u.x-.5-float(t))<.3&&.9>u.y)
        o+=texture(iChannel0,u/16.+vec2(c-t,15-(64+c)/16)/16.).x;}
