vec3 hsv(in float h, in float s, in float v) 

{
	return mix(vec3(1.0), clamp((abs(fract(h + vec3(3, 2, 1) / 3.0) * 6.0 - 3.0) - 1.0), 0.0 , 1.0), s) * v;
}	

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec3 p = -1.0 + 2.0 * vec3(fragCoord.xy, 1.0) / vec3(iResolution.xy, 1.0);
	p.x *= iResolution.x / iResolution.y;
	vec3 c = vec3(-iTime*0.0754, iTime*0.0985, iTime*0.0893);
	//c = 8.0*iMouse.xyy/iResolution.xxy;
	vec3 col = vec3(0);
	float t = iTime;
	for (int i = 0; i < 11; i++) 
	{
		p = abs(mod(p/dot(p,p)+c, 2.0) - 1.0);
			col += hsv(p.x, max(1.0-atan(acos(p.z/p.y),asin(p.y/p.x)), 0.0), 1.0);
	}
	fragColor = vec4(sin(col)*0.5+0.5,
			    		1.0);
}
