vec3 colorA = vec3(1.0, 0.0, 0.0);
vec3 colorB = vec3(0.0, 0.0, 1.0);

vec3 linearstep(vec3 colorA, vec3 colorB, float edge, float x)
{
    if( edge<x )return colorA;
    else return colorB;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 y = linearstep(colorA, colorB, uv.x, iMouse.x/iResolution.x);
    //float y = linearstep(uv.x, iMouse.x);
	vec3 color = vec3(0., 0., 0.);
    color = vec3(y);
    fragColor = vec4(color,1.0);
}
