#define PI 3.14159265359

float hardWave(in vec2 xy, in float amplitude, in float frequency, in float phase, in float speed, in float thickness) {
    float delta = thickness / 2.;
    float value = amplitude * sin(.56 * PI * frequency * xy.x + iTime * speed + phase);
    float deviation = xy.y - value;
    return (step(-delta, deviation) - step(delta, deviation)) * .7;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2. * (fragCoord.xy - .5 * iResolution.xy) / iResolution.y;

    fragColor = vec4(0.);
    float time = iTime + 204.;

    for(float i = 1.; i < 20.; i++) {
        uv.y += .06 * sin(uv.x * .7 * time);

        //fragColor += hardWave(uv, .1, 1., 0., 1., .05) * .3 * vec4(.7, .2, 1., 1.);
        fragColor += hardWave(uv, .1, 1., 0., 1., .05) * .3 * vec4(sin(time), .2, sin(time / 2.), 1.);

    }

}
