void mainImage( out vec4 o, in vec2 i )
{
    vec2 t, s=vec2(i/iResolution.xy);

    float y = -s.y+.2, x, z = 0., H;
    o = texture(iChannel1, s) - .1;

    for(; z < 10. && t.x < .1; z+=.01){
        x = (s.x-.5)*.7 + z*.03;

        t = vec2(x*z,z + iTime*.3);

        vec4 h = texture(iChannel0, .3*t);
        H = h.x/z - z*z*.02;
        if(H < y) {
            //o = h*2.*(x+.5);
            //o = h*.5;
            //o = h.yyyy*3. * (-y + h.x/z - z*z*.02 + .3);
            //o = h*10. * (-h.x + s.y+.3)/z * (x+.5);
            o = h*10. * (H-y + .2) * (x+.6);
            break;
        }
    }

}
