const float sinB = 3.14159 * 2.;
const float deg = 180. / 3.14159;

const vec4 K = vec4(1., 2. / 3., 1. / 3., 3.);
vec3 hsv2rgb(vec3 c)
{
	// from http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl
    vec3 p = abs(fract(c.xxx + K.xyz) * 6. - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0., 1.), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.x;
	vec2 pan = iMouse.xy / iResolution.x;
    float t = iTime * 5.;

    vec4 m_sample = texture(iChannel0,abs(uv - pan));
    float d = distance(uv,pan);
    float x = (d * d * sinB * (m_sample.x * 2. + 3.)) * 10.; 
    float v = sin(x - t) * 0.25 + 0.5;

	vec2 p = vec2(.5,.5) - pan;
    float a = atan(p.x,p.y) * deg;
    float h = a / 360.;

    float spec = texture(iChannel1,abs(uv - pan - vec2(.5,.5))).x;

    fragColor = vec4(hsv2rgb(vec3(h,distance(pan,uv),1.- v - (spec * 0.75))), 1.);
}
