float renderAlpha(float alpha) {
   alpha = pow(alpha, 1.0);
   if (alpha > 1.0)  {       // Inside  
     return 1.0;
   } else if (alpha < 0.0) { // Outside  
     return 0.0;
   } else {                  // Near boundary  
     return alpha;
   }   
}

const float PI_2 = 2.0 * 3.14159265358979323846264;

float simpleCircle(vec2 position) {
    float dist = dot(position, position);
    return 1.0/(sqrt(dist) * 1000.0);
}

float circleGrid(vec2 gridSize, vec2 position) {
   vec2 q = mod(position, gridSize) - (gridSize * 0.5);
   return position.x * simpleCircle(q);
}

