// See "Combustible Voronoi"
// https://www.shadertoy.com/view/4tlSzl
vec3 firePalette(float i){

    float T = 1400. + 1200.*i; // Temperature range (in Kelvin).
    vec3 L = vec3(7.4, 5.6, 4.4); // Red, green, blue wavelengths (in hundreds of nanometers).
    L = pow(L,vec3(5.0)) * (exp(1.43876719683e5/(T*L))-1.0);
    return 1.0-exp(-7e8/L); // Exposure level. Set to "50." For "70," change the "5" to a "7," etc.
}

// See "Combustible Voronoi"
// https://www.shadertoy.com/view/4tlSzl
vec3 bluefirePalette(float i){

    float T = 1900. + 1200.*i; // Temperature range (in Kelvin).
    vec3 L = vec3(6.4, 5.6, 7.4); // Red, green, blue wavelengths (in hundreds of nanometers).
    L = pow(L,vec3(5.0)) * (exp(1.43876719683e5/(T*L))-1.0);
    return 1.0-exp(-8e7/L); // Exposure level. Set to "50." For "70," change the "5" to a "7," etc.
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    //fragColor = vec4(bluefirePalette(simpleCircle(uv - vec2(0.5, 0.5))), 1.0);
    fragColor = vec4(firePalette(0.1 * circleGrid(vec2(0.001, 0.001), uv)), 1.0)
              + vec4(bluefirePalette(0.1 * circleGrid(vec2(0.001, 0.001), uv + vec2(0.0001, 0.0))), 1.0);
}
