void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x = cos((uv.x + iTime*(iMouse.x/1000.0)) * 3.14159*20.0)*0.5 + 0.5;
    uv.y = sin((uv.y + iTime*(iMouse.y/100.0)) * 3.14159*2.0)*0.5 + 0.5;
	fragColor = vec4(uv,cos(iTime*2.0*3.14159*(iMouse.x/100.0))*0.5 + 0.5,1.0);
}
