float rnd(vec2 s)
{
    return 1.-2.*fract(sin(s.x*678.53+s.y*313.24)*87.19);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p=(fragCoord.xy*2.-iResolution.xy)/iResolution.x;
    float col=0.;

    vec2 v=vec2(1E3);
    for(int c=0;c<40;c++)
    {
        vec2 center=vec2(0.,-.5);
        vec2 vc=vec2(center.x+rnd(vec2(float(c),2.23))*.5*cos(floor(rnd(vec2(float(c),4.7))*4.)*0.7854)+rnd(vec2(float(c),349.3))*.01,
                     center.y+rnd(vec2(float(c),2.23))*.5*sin(floor(rnd(vec2(float(c),4.7))*4.)*0.7854)+rnd(vec2(float(c),912.7))*.01);
        float d=abs(length(dot(p-v,normalize(v-vc)))-length(dot(p-vc,normalize(v-vc))));
        if(d<3E-3)
        {
            col=clamp(2E-4/d,0.,1.);
            break;
        }
        if(length(p-vc)<length(p-v))
        {
	        v=vc;
        }
    }

    vec4 tex=vec4(texture(iChannel0,fragCoord.xy/iResolution.xy));
    fragColor=col*vec4(vec3(1.-tex.xyz),1.)+(1.-col)*tex;
}
