/**
 * perception challenge
 *
 * by David Jonsson
 */

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    fragColor = vec4(sin(fragCoord.x * iTime/exp(1.)), sin(fragCoord.y * iTime/asin(1.)), sin((fragCoord.x + fragCoord.y) * iTime/2.0), 1); 

}

vec2 mainSound( in int samp,float time)
{
  return vec2(sin(1./(time+0.01))+ sin( 6021.*time)+sin(23.*time*time)+sin( 41.*time)*sin(9.*time*time*time));    
}
