void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragColor = texture(iChannel0, fragCoord/iResolution.xy);

    vec2 p = fragCoord.xy / iResolution.xy;

    p.x /= 300.0;
    //p.y /= sin(iTime/5.0);
    p.y /= 4.0;
    p.x -= iTime/150.0;

//    float x = sin(5.0*p.y) * 0.15;
    float y = sin(1.5*(p.x + iTime/150.0)*300.0) * 0.12;

//    p.x += x;
    p.y += y;

    fragColor = texture(iChannel0, p);
    fragColor = vec4(vec3((fragColor.x + fragColor.y + fragColor.z)/3.0), fragColor.a);
    fragColor *= vec4(1.6,1.6,1.6,1.6);
    if (fragColor.x + fragColor.y + fragColor.z < 1.2)
        fragColor = vec4(0.0);
}
