void mainImage( out vec4 o, vec2 p ) {
	float a = 1e7, j;
	vec3 d = vec3( p/iResolution.xy, 1 ) - .6, g = vec3( 0, 9, -99. + mod( iTime * 5., 2e2 ) );
	o = vec4( 1, 1, -1, -1 );

    for ( j = 0. ; j < 9. ; j+=.1 )
		for ( int i = 0 ; i < 4; i++ )
			a = min( a, d.y < 0. ? length( g.xz + d.xz * ( j - g.y ) / d.y - o.xy * exp( j ) ) : a ),
			o = o.yzwx;
	o = vec4( .4/a, .3/a, a/99., 1 );
}
