void mainImage( out vec4 c, vec2 u ) {

    c.xy = iResolution.xy;
    vec3 h, n, d = vec3((u-c.xy*.5)/c.x,.7);

    float i, j, p, a, t=i=0.;
    for (;i++<2e2;) {
        h = d*t*.05;
        h.z -= 6.;

        p = 6.;
        n -= n;

        for (j=.5;j++<9.;)
            h -= sin(vec3(8,4,9)*j+iTime)*.4*j,
            a = dot(h,h),
            p -= 1./a, // metaballs potential
            n += h/a/a; // gradient

        t+=p;

        if (p<.1) d+=n*.3; // worst reflect ever

    }

    c = texture(iChannel0, d);

}
