void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float freq = 20.0;
    float amp = 20.0 - sin(iTime / 5.0) * 15.0;
    float pos = 0.5;

    float waveinput = uv.x + sin(iTime / 5.0);
    float wave = (sin(waveinput * freq)) / amp + pos;

    float lineWidth = 0.001;
    float smoothWidth = 1.0 - sin(iTime / 5.0) * 0.2;

    //float lineBottom = smoothstep(wave - lineWidth - smoothWidth, wave - lineWidth, uv.y);
    //float lineTop = smoothstep(wave + lineWidth + smoothWidth, wave + lineWidth, uv.y);
    float line = smoothstep( lineWidth + smoothWidth,  lineWidth, abs(uv.y-wave));

    fragColor = vec4(fract(line * 6.0 + 0.8));
}
