void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 color = texture(iChannel0, uv); 
	fragColor = color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    float target = 1.- floor(0.99+length(iMouse.xy/iResolution.xy - uv));

    vec4 color = vec4(target);
    vec4 color2 = vec4(target);
    float noise = texture(iChannel1, uv+vec2(0., iDate.w)).x;
    noise = mix(0.96, 1., noise);
    color = max(noise*texture(iChannel0, uv+vec2(0.,0.001)),vec4(target)); //2.5

    color2 = max(noise*2.5*texture(iChannel0, uv+vec2(0.,0.0004)),vec4(target)); //2.5

    fragColor = mix(color,color2,floor(uv.x+0.9));//vec4(floor(uv.x+0.5));
}

