void mainImage( out vec4 o, in vec2 p )
{
    vec3 R = iResolution;
    float T = iTime;
    p=(p-R.xy/2.)/min(R.x,R.y);

	o = vec4(grass(p) * wind(p, iTime * 2.), 1.0);

