float wave(float p, float freq, float amp){
	return sin(p * freq + iTime)*amp;    
}

const int num = 6;
float xoff[num];
float yoff[num];
vec4 mouse;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iMouse == vec4(0)){
    	mouse = vec4(300, 300, 0, 0);    
    } else {
    	mouse = iMouse;    
    }
	vec2 uv = fragCoord / iResolution.xy;

    uv -= vec2(0.5);
    uv.x *= iResolution.x/iResolution.y;
    uv += vec2(0.5);

    float t = iTime;
    float r = 0.5;

    float df = 0.0;
    float ef = 0.0;
    float ff = 0.0;

    for (int i = 0; i < num; i++){
    	float angle = 6.28/float(num) * float(i);
        xoff[i] = cos(angle) * r + (0.5);
        yoff[i] = sin(angle) * r + (0.5);
        float x = abs(uv.x-xoff[i]);
        float y = abs(uv.y-yoff[i]);
        float d = sqrt(x*x+y*y);
        float e = (x*y);
        float f = dot(x, y);
        df += wave(d, mouse.x/4.0, 1.0);
        ef += wave(e, mouse.y, 1.0);
        ff += wave(f, (mouse.x + mouse.y)/16.0, 1.0);
    }

    float b = sin(df);
    float c = cos(ef);
    float d = tan(ff);
    b = smoothstep(0.1, 0.9, b);
    c = smoothstep(0.1, 0.9, c);
    d = smoothstep(0.1, 0.9, d);
    //float z = smoothstep(c, b, d);
    //fragColor = vec4(b, c, d, 1.0);

    float z = mix(b, c, d);
    fragColor = vec4(vec3(z), 1.0);


}
