float box(vec4 c, vec2 uv) {
    float left = step(c.x, uv.x);
    float bottom = step(c.y, uv.y);
    float right = step(1. - c.z, 1.- uv.x);
    float top = step(1. - c.w, 1. - uv.y);

    return left * bottom * right * top; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    vec3 color = vec3(0.);

    float box1 = box(vec4(.0, 0.0, 2., .2), st);
	float box2 = box(vec4(.0, 0.2 + sin(iTime + 1.)/64. - 0.03, 2., .4), st);
    float box3 = box(vec4(.0, 0.4 + sin(iTime + 7.)/64. - 0.03, 2., .6), st);
    float box4 = box(vec4(.0, 0.6 + sin(iTime + 10.)/64. - 0.03, 2., .8), st);
    float box5 = box(vec4(.0, 0.8 + sin(iTime + 12.)/64. - 0.03, 2., 1.), st);

    color = mix(color, vec3(53., 62., 66.)/255., box1);
    color = mix(color, vec3(89., 106., 114.)/255., box2);
    color = mix(color, vec3(189., 94., 64.)/255., box3);
    color = mix(color, vec3(245., 235., 233.)/255., box4);
    color = mix(color, vec3(163., 151., 151.)/255., box5);

    fragColor = vec4(color,1.0);
}
