mat2 rotate(in float a) {
  return mat2(cos(a),-sin(a),sin(a),cos(a));   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 p = -1. + 2. * uv;
    	p.x *= iResolution.x / iResolution.y;

    	float mp = .5 + .5 * cos(iTime * .1);

		vec2 mp2 = vec2(cos(iTime) * 5., sin(iTime) * 5.);

    	p *= length(mix(cos(p + mp2), p, mp));

    	p = mod(p,.75) * mod(-p, .75) * 1./length(p);

		p = .0125 * cos(mix(p, cos(p) * .1, mp));    

    float x = 1./length(p) + iTime * .1;
    float y = atan(p.x,p.y);

    vec3 c = texture(iChannel0, vec2(x,y)).xyz;

    c = (c + .5) * c * c;

	fragColor = vec4(c,1.0);
}
