void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float distOut = 10. - sqrt(distance(iResolution.xy/2., fragCoord));
    distOut = clamp(distOut, 0., 1.);
    float distIn = 9.0 - sqrt(distance(iResolution.xy/2., fragCoord));
    distIn = clamp(distIn, 0., 1.);
	vec4 circle = vec4(distOut - distIn);
    vec4 color = vec4(fragCoord.x / iTime - fragCoord.y * iTime, 1. - clamp(fragCoord.x / iTime - fragCoord.y * iTime, 0., 1.), 0.0, 0.0);
    fragColor = clamp(circle, 0., 1.) * clamp(color, 0., 1.);
}
