#define SCREENWIDTH 340.0
#define SCREENHEIGHT 340.0
vec2 Pixelize(vec2 fragCoord)
{
    fragCoord.x *= SCREENWIDTH;
    fragCoord.y *= SCREENHEIGHT;

    fragCoord.x = ceil(fragCoord.x);
    fragCoord.y = ceil(fragCoord.y);

    fragCoord.x /= SCREENWIDTH;
    fragCoord.y /= SCREENHEIGHT;

    return fragCoord;
}


vec4 RGBifiy(vec4 inColor, vec2 fragCoord)
{
   	vec2 PixelUV;
    PixelUV.x = fragCoord.x / (1.0 / SCREENWIDTH);
    PixelUV.x = mod(PixelUV.x, 3.0);
    PixelUV.y = fragCoord.y / (1.0 / SCREENHEIGHT);
    PixelUV.y = mod(PixelUV.y, 5.0);

    float redValue = 0.0;
    float greenValue = 0.0;
    float blueValue = 0.0;
    if(PixelUV.x < 1.0)
    {
    	redValue = inColor.r;
    }
    if(PixelUV.x > 1.0 && PixelUV.x < 2.0)
    {
    	greenValue = inColor.g;
    }
    if(PixelUV.x >= 2.0)
    {
    	blueValue = inColor.b;
    }
    vec4 outColor = vec4(0);

    outColor += vec4(redValue,greenValue,blueValue, 1.0);

    if(PixelUV.y >= 4.0)
    {
        outColor *= 0.3;
    }

    return outColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 pixeledUV = Pixelize(uv);

    vec4 finalColor = vec4(0);
    vec4 image = texture(iChannel0, pixeledUV);
    finalColor += RGBifiy(image, uv) * 1.3;


    fragColor = finalColor;//finalColor;
}
