float factor=2.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv =  fragCoord.xy/iResolution.xy;
    float t=iTime;
    float freq=texture(iChannel0,uv).x;

    uv.x/=1.04;

    vec3 tx=texture(iChannel0,uv).rgb*(factor*factor*freq)/factor;
         tx.r+=mod(0.+t/20.,.1+sin(t));

        // color calculation much better now to warm texture paradox !!.. 




    fragColor = vec4(vec3(tx),1.0);



}
