void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 col = vec4(0.0);

    if(uv.x >.5)
        col=vec4(1.0);

    if(uv.x<.65 && uv.x>.35 && uv.y<.65 && uv.y>.35)
        col=vec4(.5);

	fragColor = vec4(col);
}
