float plot(float y,float sty){

	return smoothstep(y-0.01,y,sty)-smoothstep(y,y+0.01,sty);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy+ vec2(0,-10)) / iResolution.xy;

	float y = smoothstep(0.3,0.5,uv.x) - smoothstep(0.5,0.7,uv.x);
	      y = y - smoothstep(0.8,1.0,uv.y);

	y=plot(y,uv.y);
	vec3 color = vec3(y);

	fragColor = vec4(color,1.0);
}
