//Thanks to Greg Rostami for the size optimizations
void mainImage(out vec4 o,vec2 i)
{
    o = vec4(0, i /= iResolution.x, 0);
    for (float k = 1.; k < 7.; k++)
        o = sin(o.wxyz+iDate.w*.1/k)/dot(normalize(o),normalize((i=cos(o.yz+i)).yxyx))-o;
    o = pow(o/32.,vec4(.8));
}

