  int read(vec2 p){
      vec3 v = texture(iChannel0,p/iResolution.xy).xyz;
      return v.x > 0.5 ? 1 : 0;
  }

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  //fragCoord.x--;
  vec3 c = texture(iChannel0,fragCoord.xy/iResolution.xy).xyz;
  fragColor.xyz = c;
    if(iFrame <= 0){
        //åˆæœŸåŒ–
        fragColor.xyz = rand(fragCoord.y + fragCoord.x + fragCoord.x*fragCoord.y);

    }else{

    	int cnt = 0;

        for(float i = -1.; i < 2.; i++){
            for(float j = -1.; j < 2.; j++){
            if(!(i == 0.&& j == 0.) && read(fragCoord + vec2(i,j))==1)cnt++;
            }
        }

        if(read(fragCoord) ==1 && (cnt == 2 || cnt == 3)){
             c = vec3(1.);

        }else if(read(fragCoord) ==0 && cnt == 3){
        	 c = vec3(1.);

        }else{
        	 c = vec3(0.);
        }

        fragColor.xyz = c;
    }

  fragColor.w = 1.;
}
