void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 clr = texture(iChannel0,uv).rgb;
    float origG = clr.g;
    clr.g = min(clr.g,max(clr.r,clr.b)); // Remove the green screen

	fragColor = clamp(texture(iChannel1, uv+(1.0-clr.rg)*.2-0.187) // bend the texture with JCvD colors
                      *(0.75+0.7*abs(origG-clr.g)) // Make him darker
                      +0.2*clr.rgbb,0.0,1.0); // Add some of his colors
}
