float stroke(float x, float s, float w) {
	float d = step(s,x+w*.5) - step(s, x-w*.5);
    return clamp(d, 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = vec3(0.);

    color += stroke(uv.x, .5, .15);

    fragColor = vec4(color,1.0);
}
