float Circle(vec2 uv, vec2 p, float r, float blur) {
	float d = length(uv - p);
    float c = smoothstep(r, r-blur, d);

    return c;
}

float rand(float n){
    return fract(cos(n*89.42)*343.42);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  	float scale = iResolution.x/iResolution.y;

	vec2 uv = fragCoord.xy / iResolution.xy;

	uv -= .5;

    uv.x *= scale;

    float originY = cos(0.5 * (iTime));
    float originX = scale * originY;

    float c1 = rand(length(uv)) * Circle(uv, vec2(originX, originY), .3, .01);

    float c2 = rand(length(uv)) * Circle(vec2(-uv.x, uv.y), vec2(originX, originY), .3, .01);

    float c3 = rand(length(uv)) * Circle(vec2(-uv.x, -uv.y), vec2(originX, originY), .3, .01);

    float c4 = rand(length(uv)) * Circle(vec2(uv.x, -uv.y), vec2(originX, originY), .3, .01);


    float c = c1 + c2 + c3 + c4;

	fragColor = vec4(abs(sin(iTime)), c, abs(cos(iTime)),1.0);

}
