void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 offset = (uv - 0.5) * 2.0;
    offset += vec2(cos(iTime * 1.2), sin(iTime * 1.5));

    float xColor = cos(length(offset) + iTime) + 0.5;
    float yColor = sin(length(offset) + iTime) + 0.5;

    fragColor = vec4(xColor, (xColor + yColor) / 2.0, yColor, 1.0);
    fragColor *= dot(uv, offset);
    fragColor += abs(log2(fragColor)) / 10.0;
}
