void mainImage(out vec4 c, vec2 p)
{
    //setup pixels size and ratio
 	vec3 R = iResolution;              
    p  =  1. - 2. * p / R.xy; 
    p.x *= R.x / R.y;


    //store pixels length    
  	float l = length(p);  

	// distord space

    p   /= abs(.3 - dot(p, p))* .05;
    p.x -= cos(p.y ) + iTime * 9. - tan(l - iTime);


	// apply pattern
    c = vec4(smoothstep(.05, 0., mix(sin(p.x) * cos(p.y), .2, cos(p.x) * .3)));

	// contrast 
    c -= sin(atan(l) * 3.);

    // color
    c  *= .2/ vec4(.4 * l, .9 * l, l / 6., 1.) ;
    // c  *= abs(sin(iTime)*.5) / vec4(.4 * l, .9 * l, l / 6., 1.) ;


}
