void mainImage( out vec4 color, in vec2 uv )
{
    float t = mod(3.*iTime,1.5)-1.;
    t = -20.*t*exp(-40.*t*t);

    vec2 p;
    float h;


    p=(4.+0.5*t)*(uv/iResolution.xy-0.5);
    p.x*=iResolution.x/iResolution.y;
    h = p.x*p.x+p.y*p.y-1.;
    h=h*h*h;
    h-=p.x*p.x*p.y*p.y*p.y;
    h=step(0.,h);  
   	color = mix(vec4(1.,0.,0.,0.),vec4(1.0),h);

}
