void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec4 sum = vec4(0);
	vec2 uv = fragCoord.xy / iResolution.xy;
	int i, j;
	for(i=-4; i< 4; i++) {
		for(j=-3; j< 3; j++) {
			sum += texture(iChannel0, uv+vec2(j, i)*0.004)*0.25;
		}
	}
	if(texture(iChannel0, uv).r < 0.3) {
		fragColor = sum*sum*0.012+texture(iChannel0, uv);
	} else {


		if (texture(iChannel0, uv).r < 0.5)
        {
            fragColor = sum*sum*0.009 + texture(iChannel0, uv);
        }
        else
        {
            fragColor = sum*sum*0.0075 + texture(iChannel0, uv);
        }
	}

}
