#define time iTime

float wat(vec2 uv) {
    return cos(dot(uv, vec2(213.124, 1234.12222)*10.1241));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    vec3 col = vec3(1., 1., .2);
    vec3 col2 = vec3(1., .2, .1);
    vec2 loc = vec2(.6, .5);

    uv.x += wat(uv)/(10.+cos(time)*10.);
    uv += vec2(sin(uv.x+time), cos(uv.y+time))/10.;

    col2 *= 1.0 - distance(uv, loc)/1.2;
    col *= 1.0-smoothstep(.2, .28-cos(time)/20., distance(uv, loc));
    col = mix (col, col2,.2+wat(uv)/10.);

    fragColor = vec4(col,1.0);
}
