float RaySphereGetT(vec3 rs, vec3 rd, vec3 sc, float r){

    vec3 v = (rs-sc);

    float a = dot((v+v), rd);

    float disc = a*a - 4.0*(dot(v,v)-(r*r));

    if(disc < 0.0)return -1.0;

    float t = (-(dot(v+v, rd)-sqrt(disc)))/2.0;

    return t > 0.0 ? t : -1.0;
}

bool sphereRay(vec3 rs, vec3 rd, vec3 sc, float r){

    vec3 v = (rs-sc);

    float a = dot((v+v), rd);

    float disc = a*a - 4.0*(dot(v,v)-(r*r));

    if(disc < 0.0)return false;

    float t = (-(dot(v+v, rd)-sqrt(disc)))/2.0;

    return t > 0.0;
}


vec3 getColor(vec3 rs, vec3 rd, vec3 sc, float r){

    float t = RaySphereGetT(rs, rd, sc, r);

    if(t == -1.0)return  vec3(.8, .8, .8);
    else if(t > 8.5)return vec3(1.0, 0.0, 0.0);
    else if(t > 7.0)return vec3(.5, 0.0, 0.0);
    else return vec3(0.2, 0.0, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y = ((uv.y-0.5)*0.66) + 0.5;
    vec3 rs = vec3(0.0, 0.0, 0.0);
    vec3 rd = normalize(vec3((4.5)*(uv.x-0.5), (4.5)*(uv.y-0.5), 1.0));

    vec3 sc = vec3(.0, 0, 10.0);
    float r = 5.0; 
    vec3 lightPos = vec3((sin(iTime))*5.0, .5, -cos(iTime)*5.0);
    vec3 lookDirection = vec3(0.0, 0.0, 1.0);
    vec3 lightToSphere = normalize(lightPos-rd);


    vec3 color = vec3(.8, .8, .8);

    if(sphereRay(rs, rd, sc, r)){
        float light = max((dot(rd,lightToSphere)), 0.25);

     	color = vec3(light, 0, 0);
    }


	fragColor = vec4(color,1.0);
}
