vec4 posterize(vec4 c, float ln)
{
    return floor(c*ln)/ln;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 pxy = (fragCoord.xy - 0.5*iResolution.xy) / iResolution.x;
	fragColor = posterize(vec4(length(pxy),
                               abs(0.99*sin(-iTime+length(pxy)*10.0)),
                               abs(0.99*sin(iTime+length(pxy)*10.0)), 1), 8.0);
}
