#define PI 3.1415926535897932384626433832795

float max_dist = distance(vec2(0.5, 0.5), vec2(1.0, 0.5));


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float dist = distance(uv, vec2(0.5, 0.5)) / max_dist;
    float a = (atan(uv.y - 0.5, uv.x - 0.5) / PI + 1.0) / 2.0;

    fragColor = texture(iChannel0, vec2(a, dist));
}
