void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy - vec2(0.5,0.5));
    float a = uv.x; //atan(uv.y,uv.x);

    float tt=iTime+a*0.008;

    vec2 a1 = s1(tt);
    vec2 a2 = s2(tt);
    vec2 a3 = s3(tt);


    float dist = 10.00;
    float zoom = 40.0;
    float amp = 0.3;

    if (a1.x>0.0) {
    a1.x = 500.0+a1.x;
    dist = min( dist, abs(((a1.x/100.0)+(amp*a1.y)) - uv.y*zoom) );
    }
    if (a2.x>0.0) {
    a2.x = 1000.0-a2.x;
    dist = min( dist, abs(((a2.x/100.0)+(amp*a2.y)) - uv.y*zoom) );
    }

    if (a3.x>0.0) {
    a3.x = 00.0-a3.x;
    dist = min( dist, abs(((a3.x/100.0)+(amp*a3.y)) - uv.y*zoom) );
    }

    fragColor = vec4(vec3(dist),1.0);

    //dist=dist / 8.0;
    //fragColor = vec4( sin(dist*3.141*2.5), sin(dist*3.141*2.0),sin(dist*3.141*1.0),1.0);
}

